import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import ChatRoomUI from './ChatRoomUI';
import axios from 'axios';
import DOMPurify from 'dompurify';

let stompClient = null;

const ChatRoom = () => {
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState("CHATROOM");
  const [unreadMessages, setUnreadMessages] = useState(new Map());
  const [allUsers, setAllUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  const [userData, setUserData] = useState({
    username: '',
    password: '',
    email: '',
    receivername: '',
    connected: false,
    message: '',
    errors: {}
  });
  const [isRegistering, setIsRegistering] = useState(false);

  useEffect(() => {
    if (userData.connected) {
      connect();
      fetchAllUsers();
      loadChatHistory();


    }
  }, [userData.connected]);

  useEffect(() => {
    if (tab !== "CHATROOM" && userData.connected) {
      loadPrivateChatHistory(tab);
    }
  }, [tab]);

  /*
  const loadPrivateChatHistory = async (receiverName) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/chats/history?senderName=${userData.username}&receiverName=${receiverName}`);
      setPrivateChats(new Map(privateChats.set(receiverName, response.data)));
    } catch (error) {
      console.error('Error loading private chat history:', error);
    }
  };*/
  
  const loadPrivateChatHistory = async (receiverName) => {
    try {
        const response = await axios.get(`http://localhost:8080/api/chat/history?senderName=${userData.username}&receiverName=${receiverName}`);
        const sortedMessages = response.data.sort((a, b) => new Date(a.date) - new Date(b.date));
        setPrivateChats(new Map(privateChats.set(receiverName, sortedMessages)));
    } catch (error) {
        console.error('Error loading private chat history:', error);
    }
  };
  


  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({username: userData.username}, onConnected, onError);
  }

  const onConnected = () => {
    setUserData({ ...userData, connected: true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    userJoin();
    fetchAllUsers();
    loadChatHistory();
  }

  const userJoin = () => {
    var chatMessage = {
      senderName: userData.username,
      status: "JOIN"
    };
    stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
  }
  const fetchAllUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users/all');
      setAllUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const sendPrivateValue = () => {
    if (stompClient) {
        var chatMessage = {
            senderName: userData.username,
            receiverName: tab,
            message: userData.message,
            status: "MESSAGE"
        };

        if (userData.username !== tab) {
            let msgs = privateChats.get(tab) || [];
            msgs.push(chatMessage);
            setPrivateChats(new Map(privateChats.set(tab, msgs)));
        }
        stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
        setUserData({ ...userData, message: "" });
    }
}
