import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import ChatRoomUI from './ChatRoomUI';
import axios from 'axios';

let stompClient = null;

const ChatRoom = () => {
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState("CHATROOM");
  const [unreadMessages, setUnreadMessages] = useState(new Map());
  const [userData, setUserData] = useState({
    username: '',
    password: '',
    email: '',
    receivername: '',
    connected: false,
    message: '',
    errors: {}
  });
  const [isRegistering, setIsRegistering] = useState(false);

  useEffect(() => {
    if (userData.connected) {
      connect();
    }
  }, [userData.connected]);

  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({username: userData.username}, onConnected, onError);
  }

  const onConnected = () => {
    setUserData({ ...userData, connected: true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    userJoin();
  }

  const userJoin = () => {
    var chatMessage = {
      senderName: userData.username,
      status: "JOIN"
    };
    stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
  }

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    switch (payloadData.status) {
      case "JOIN":
        if (!privateChats.get(payloadData.senderName)) {
          privateChats.set(payloadData.senderName, []);
          setPrivateChats(new Map(privateChats));
        }
        break;
      case "MESSAGE":
        publicChats.push(payloadData);
        setPublicChats([...publicChats]);
        break;
    }
  }

  const onPrivateMessage = (payload) => {
    var payloadData = JSON.parse(payload.body);
    if (privateChats.get(payloadData.senderName)) {
      privateChats.get(payloadData.senderName).push(payloadData);
      setPrivateChats(new Map(privateChats));
    } else {
      let list = [];
      list.push(payloadData);
      privateChats.set(payloadData.senderName, list);
      setPrivateChats(new Map(privateChats));
    }

    if (tab !== payloadData.senderName) {
      setUnreadMessages(prevUnreadMessages => {
        const newUnreadMessages = new Map(prevUnreadMessages);
        const count = newUnreadMessages.get(payloadData.senderName) || 0;
        newUnreadMessages.set(payloadData.senderName, count + 1);
        return newUnreadMessages;
      });
    }
  }

  const handleTabChange = (name) => {
    setTab(name);
    setUnreadMessages(prevUnreadMessages => {
      const newUnreadMessages = new Map(prevUnreadMessages);
      newUnreadMessages.delete(name);
      return newUnreadMessages;
    });
  };

  const onError = (err) => {
    console.log(err);
  }

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, message: value });
  }

  const sendValue = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: userData.username,
        message: userData.message,
        status: "MESSAGE"
      };
      stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: "" });
    }
  }

  const sendPrivateValue = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: userData.username,
        receiverName: tab,
        message: userData.message,
        status: "MESSAGE"
      };

      if (userData.username !== tab) {
        privateChats.get(tab).push(chatMessage);
        setPrivateChats(new Map(privateChats));
      }
      stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: "" });
    }
  }

  const handleUsername = (event) => {
    const { name, value } = event.target;
    // Solo permite letras en el campo de usuario
    const filteredValue = value.replace(/[^a-zA-Z]/g, '');
    setUserData({ ...userData, [name]: filteredValue });
  }

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
  };

  const validateUsername = (username) => {
    const re = /^[a-zA-Z]+$/;
    return re.test(username);
  };

  const validateFields = () => {
    const errors = {};
    if (!userData.username || !validateUsername(userData.username)) {
      errors.username = "El nombre de usuario solo puede contener letras";
    }
    if (!userData.password) {
      errors.password = "La contraseña no puede estar vacía";
    }
    if (!userData.email || !validateEmail(userData.email)) {
      errors.email = "El email no es válido";
    }
    return errors;
  };

  const registerUser = async () => {
    const errors = validateFields();
    if (Object.keys(errors).length > 0) {
      setUserData({ ...userData, errors });
      return;
    }
    try {
      const response = await axios.post('http://localhost:8080/api/auth/register', {
        username: userData.username,
        password: userData.password,
        email: userData.email
      });
      console.log(response.data);
      setIsRegistering(false);
    } catch (error) {
      console.error('Registration failed:', error.response.data);
    }
  }

  const loginUser = async () => {
    const errors = validateFields();
    if (Object.keys(errors).length > 0) {
      setUserData({ ...userData, errors });
      return;
    }
    try {
      const response = await axios.post('http://localhost:8080/api/auth/login', {
        username: userData.username,
        password: userData.password
      });
      console.log(response.data);
      setUserData({ ...userData, connected: true });
    } catch (error) {
      console.error('Login failed:', error.response.data);
    }
  }

  return (
    <ChatRoomUI
      userData={userData}
      privateChats={privateChats}
      publicChats={publicChats}
      tab={tab}
      setTab={handleTabChange}
      handleMessage={handleMessage}
      sendValue={sendValue}
      sendPrivateValue={sendPrivateValue}
      handleUsername={handleUsername}
      registerUser={registerUser}
      loginUser={loginUser}
      unreadMessages={unreadMessages}
      isRegistering={isRegistering}
      setIsRegistering={setIsRegistering}
    />
  );
}

export default ChatRoom;
