  const [filteredUsers, setFilteredUsers] = useState(allUsers);

  useEffect(() => {
    if (searchQuery === "") {
      setFilteredUsers(allUsers);
    } else {
      setFilteredUsers(
        allUsers.filter((user) =>
          user.username.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
    }
  }, [searchQuery, allUsers]);

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault();
      if (tab === "CHATROOM") {
        sendValue();
      } else {
        sendPrivateValue();
      }
    }
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };
  return (
    <div className="container">
      {userData.connected ? (
        <div className="chat-box">
          <div className="member-list">
            <div className="search-bar">
              <input
                type="text"
                placeholder="Buscar usuarios"
                value={searchQuery}
                onChange={handleSearchChange}
              />
              <button onClick={searchUsers}>Buscar</button>

            </div>
            
            <ul>
              <li
                onClick={() => setTab("CHATROOM")}
                className={`member ${tab === "CHATROOM" ? "active" : ""}`}
              >
                CHAT4FUN
              </li>
              {filteredUsers.map((user, index) => (
                <li
                  onClick={() => setTab(user.username)}
                  className={`member ${tab === user.username ? "active" : ""} ${user.username === userData.username ? "current-user" : ""} ${unreadMessages.get(user.username) ? "unread" : ""}`}
                  key={index}
                >
                  {user.username} {unreadMessages.get(user.username) ? `(${unreadMessages.get(user.username)}) Nuevo Mensaje` : ''}
                  {user.username === userData.username && <span className="current-user-indicator">(Tú)</span>}
                </li>
              ))}
            </ul>
          </div>
          

          <div className="chat-content">
            
            <header className="chat-header">
              
              {tab === "CHATROOM" ? "CHATROOM" : `Chat con ${tab}`}
            </header>
            
            <ul className="chat-messages">
              {(tab === "CHATROOM" ? publicChats : privateChats.get(tab) || []).map((chat, index) => (
                <li className={`message ${chat.senderName === userData.username ? "self" : ""}`} key={index}>
                  <div className="sender-name">{chat.senderName}</div>
                  <div className="message-data">{chat.message}</div>
                </li>
              ))}
            </ul>
            <div className="send-message">
              <input
                type="text"
                className="input-message"
                placeholder="Escribe un mensaje"
                value={userData.message}
                onChange={handleMessage}
                onKeyDown={handleKeyDown}
                required
              />
              <button type="button" className="send-button" onClick={tab === "CHATROOM" ? sendValue : sendPrivateValue}>Enviar</button>
            </div>

          </div>
          
          <div className='contenedor'>
          <button className="logout-button" onClick={logoutUser}>Cerrar sesión</button>
            </div>
        </div>
        
      ) : (
        
        <div className="register">
          <h1>Bienvenido a CHAT4FUN</h1>
          {userData.errors && Object.keys(userData.errors).length > 0 && (
            <div className="error-messages">
              {Object.values(userData.errors).map((error, index) => (
                <p key={index} className="error">{error}</p>
              ))}
            </div>
          )}
          {isRegistering ? (
            <>
              <input
                type="text"
                placeholder="Usuario"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                required
              />
              <input
                type="password"
                placeholder="Contraseña"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                required
              />
              <input
                type="email"
                placeholder="Email"
                name="email"
                value={userData.email}
                onChange={handleUsername}
                required
              />
              <button type="button" onClick={registerUser}>Registrarse</button>
              <p>¿Ya tienes una cuenta? <span onClick={() => setIsRegistering(false)}>Iniciar sesión</span></p>
            </>
          ) : (
            <>
              <input
                type="text"
                placeholder="Usuario"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                required
              />
              <input
                type="password"
                placeholder="Contraseña"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                required
              />
              <button type="button" onClick={loginUser}>Iniciar sesión</button>
              <p>¿No tienes una cuenta? <span onClick={() => setIsRegistering(true)}>Registrarse</span></p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default ChatRoomUI; 
