import React from 'react';
import '../index.css';

const ChatRoomUI = ({
  userData,
  privateChats,
  publicChats,
  tab,
  setTab,
  handleMessage,
  sendValue,
  sendPrivateValue,
  handleUsername,
  registerUser,
  loginUser,
  unreadMessages,
  isRegistering,
  setIsRegistering,
  handleSearch,
  filteredUserList
}) => {
  return (
    <div>
      {!userData.connected ? (
        <div>
          {isRegistering ? (
            <div>
              <input
                type="text"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                placeholder="Username"
              />
              <input
                type="password"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                placeholder="Password"
              />
              <input
                type="email"
                name="email"
                value={userData.email}
                onChange={handleUsername}
                placeholder="Email"
              />
              <button onClick={registerUser}>Register</button>
              <button onClick={() => setIsRegistering(false)}>Back to Login</button>
            </div>
          ) : (
            <div>
              <input
                type="text"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                placeholder="Username"
              />
              <input
                type="password"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                placeholder="Password"
              />
              <button onClick={loginUser}>Login</button>
              <button onClick={() => setIsRegistering(true)}>Register</button>
            </div>
          )}
        </div>
      ) : (
        <div>
          <input
            type="text"
            value={userData.searchQuery}
            onChange={handleSearch}
            placeholder="Search users"
          />
          <div>
            {filteredUserList.map(user => (
              <div key={user.username} onClick={() => setTab(user.username)}>
                {user.username} {unreadMessages.get(user.username) > 0 && `(${unreadMessages.get(user.username)})`}
              </div>
            ))}
          </div>
          <div>
            {tab === "CHATROOM" ? (
              <div>
                {publicChats.map((chat, index) => (
                  <div key={index}>
                    {chat.senderName}: {chat.message}
                  </div>
                ))}
                <input
                  type="text"
                  value={userData.message}
                  onChange={handleMessage}
                  placeholder="Type a message"
                />
                <button onClick={sendValue}>Send</button>
              </div>
            ) : (
              <div>
                {privateChats.get(tab) && privateChats.get(tab).map((chat, index) => (
                  <div key={index}>
                    {chat.senderName}: {chat.message}
                  </div>
                ))}
                <input
                  type="text"
                  value={userData.message}
                  onChange={handleMessage}
                  placeholder="Type a message"
                />
                <button onClick={sendPrivateValue}>Send</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default ChatRoomUI;
