import React from 'react';
import '../index.css';

const ChatRoomUI = ({
  userData,
  privateChats,
  publicChats,
  tab,
  setTab,
  handleMessage,
  sendValue,
  sendPrivateValue,
  handleUsername,
  registerUser,
  loginUser,
  unreadMessages,
  isRegistering,
  setIsRegistering,
  
}) => {
  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault(); 
      tab === "CHATROOM" ? sendValue() : sendPrivateValue();
    }
  };

  return (
    <div className="container">
      {userData.connected ? (
        <div className="chat-box">
          <div className="member-list">
            <ul>
              <li 
                onClick={() => setTab("CHATROOM")} 
                className={`member ${tab === "CHATROOM" && "active"}`}
              >
                CHAT4FUN
              </li>
              {[...privateChats.keys()].map((name, index) => (
                <li 
                  onClick={() => setTab(name)} 
                  className={`member ${tab === name && "active"} ${unreadMessages.get(name) ? "unread" : ""}`} 
                  key={index}
                >
                  {name} {unreadMessages.get(name) ? `(${unreadMessages.get(name)}) Nuevo Mensaje` : ''}
                </li>
              ))}
            </ul>
          </div>
          {tab === "CHATROOM" && <div className="chat-content">
            <ul className="chat-messages">
              {publicChats.map((chat, index) => (
                <li className={`message ${chat.senderName === userData.username ? "self" : ""}`} key={index}>
                  <div className="sender-name">{chat.senderName}</div>
                  <div className="message-data">{chat.message}</div>
                </li>
              ))}
            </ul>

            <div className="send-message">
              <input
                type="text"
                className="input-message"
                placeholder="Escribe un mensaje"
                value={userData.message}
                onChange={handleMessage}
                onKeyDown={handleKeyDown} 
                required
              />
              <button type="button" className="send-button" onClick={sendValue}>Enviar</button>
            </div>
          </div>}
          {tab !== "CHATROOM" && <div className="chat-content">
            <ul className="chat-messages">
              {[...privateChats.get(tab)].map((chat, index) => (
                <li className={`message ${chat.senderName === userData.username ? "self" : ""}`} key={index}>
                  <div className="sender-name">{chat.senderName}</div>
                  <div className="message-data">{chat.message}</div>
                </li>
              ))}
            </ul>

            <div className="send-message">
              <input
                type="text"
                className="input-message"
                placeholder="Escribe un mensaje"
                value={userData.message}
                onChange={handleMessage}
                onKeyDown={handleKeyDown}
                required
              />
              <button type="button" className="send-button" onClick={sendPrivateValue}>Enviar</button>
            </div>
          </div>}
        </div>
      ) : (
        <div className="register">
          <h1>Bienvenido a CHAT4FUN</h1>
          {userData.errors && Object.keys(userData.errors).length > 0 && (
            <div className="error-messages">
              {Object.values(userData.errors).map((error, index) => (
                <p key={index} className="error">{error}</p>
              ))}
            </div>
          )}
          {isRegistering ? (
            <>
              <input
                type="text"
                placeholder="Usuario"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                required
              />
              <input
                type="password"
                placeholder="Contraseña"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                required
              />
              <input
                type="email"
                placeholder="Email"
                name="email"
                value={userData.email}
                onChange={handleUsername}
                required
              />
              <button type="button" onClick={registerUser}>Registrarse</button>
              <p>¿Ya tienes una cuenta? <span onClick={() => setIsRegistering(false)}>Iniciar sesión</span></p>
            </>
          ) : (
            <>
              <input
                type="text"
                placeholder="Usuario"
                name="username"
                value={userData.username}
                onChange={handleUsername}
                required
              />
              <input
                type="password"
                placeholder="Contraseña"
                name="password"
                value={userData.password}
                onChange={handleUsername}
                required
              />
       
              <button type="button" onClick={loginUser}>Iniciar sesión</button>
              <p>¿No tienes una cuenta? <span onClick={() => setIsRegistering(true)}>Registrarse</span></p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default ChatRoomUI;
