import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import ChatRoomUI from './ChatRoomUI';
import axios from 'axios';
import DOMPurify from 'dompurify';

let stompClient = null;

const ChatRoom = () => {
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState("CHATROOM");
  const [unreadMessages, setUnreadMessages] = useState(new Map());
  const [allUsers, setAllUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [isReconnecting, setIsReconnecting] = useState(false);

  const [userData, setUserData] = useState({
    username: '',
    password: '',
    email: '',
    receivername: '',
    connected: false,
    message: '',
    errors: {}
  });
  const [isRegistering, setIsRegistering] = useState(false);

  useEffect(() => {
    if (userData.connected) {
      connect();
      fetchAllUsers();
      loadChatHistory();
    }
  }, [userData.connected]);

  useEffect(() => {
    if (tab !== "CHATROOM" && userData.connected) {
      loadPrivateChatHistory(tab);
    }
  }, [tab]);

  const loadPrivateChatHistory = async (receiverName) => {
    if (receiverName === userData.username && tab === receiverName) {
        return;
    }

    try {
        const response = await axios.get(`http://localhost:8080/api/chat/history?senderName=${userData.username}&receiverName=${receiverName}`);
        const sortedMessages = response.data.sort((a, b) => new Date(a.date) - new Date(b.date));
        setPrivateChats(new Map(privateChats.set(receiverName, sortedMessages)));
    } catch (error) {
        console.error('Error loading private chat history:', error);
    }
};


  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({ username: userData.username }, onConnected, onError);
  }

  const onConnected = () => {
    setUserData({ ...userData, connected: true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    stompClient.subscribe('/topic/active', onActiveUsersReceived);
    userJoin();
    fetchAllUsers();
    loadChatHistory();
  }
  const onActiveUsersReceived = (payload) => {
    const activeUsers = JSON.parse(payload.body);
    setOnlineUsers(activeUsers);
  }
  const userJoin = () => {
    var chatMessage = {
      senderName: userData.username,
      status: "JOIN"
    };
    stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
  }

  const fetchAllUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users/all');
      setAllUsers(response.data);
    } catch (error) {
      console.error('Failed to fetch users:', error);
    }
  };

  const sendPrivateValue = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: userData.username,
        receiverName: tab,
        message: userData.message,
        status: "MESSAGE"
      };

      if (userData.username !== tab) {
        let msgs = privateChats.get(tab) || [];
        msgs.push(chatMessage);
        setPrivateChats(new Map(privateChats.set(tab, msgs)));
      }
      stompClient.send("/app/private-message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: "" });
    }
  }

  const searchUsers = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/users/search`, {
        params: { query: searchQuery }
      });
      setAllUsers(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  const loadChatHistory = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/chat/history?senderName=${userData.username}&receiverName=ALL`);
      const sortedMessages = response.data.sort((a, b) => new Date(a.date) - new Date(b.date));
      setPublicChats(sortedMessages);
    } catch (error) {
      console.error('Error loading chat history:', error);
    }
  };

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    switch (payloadData.status) {
      case "JOIN":
        if (!privateChats.get(payloadData.senderName)) {
          privateChats.set(payloadData.senderName, []);
          setPrivateChats(new Map(privateChats));
        }
        fetchAllUsers();
        break;
      case "MESSAGE":
        publicChats.push(payloadData);
        setPublicChats([...publicChats]);
        break;
      case "NEW_USER":
        fetchAllUsers();
        break;
      default:
        setAllUsers([...allUsers, payloadData]);
        break;
    }
  }

  const sanitizeInput = (input) => {
    return DOMPurify.sanitize(input);
  };

  const onPrivateMessage = (payload) => {
    const payloadData = JSON.parse(payload.body);
  
    if (payloadData.senderName === userData.username && tab === userData.username) {
      return;
    }
  
    setPrivateChats(prevChats => {
      const updatedChats = new Map(prevChats);
      const currentMessages = updatedChats.get(payloadData.senderName) || [];
      updatedChats.set(payloadData.senderName, [...currentMessages, payloadData]);
      return updatedChats;
    });
  
    if (tab !== payloadData.senderName) {
      setUnreadMessages(prevUnreadMessages => {
        const newUnreadMessages = new Map(prevUnreadMessages);
        const count = newUnreadMessages.get(payloadData.senderName) || 0;
        newUnreadMessages.set(payloadData.senderName, count + 1);
        return newUnreadMessages;
      });
    }
  };

  const updateUserOnlineStatus = (username, online) => {
    setAllUsers(prevUsers => prevUsers.map(user =>
      user.username === username ? { ...user, online } : user
    ));
  };

  const renderUserStatus = (user) => {
    return user.online ? (
      <span style={{ color: 'green' }}>Online</span>
    ) : (
      <span style={{ color: 'red' }}>Offline</span>
    );
  };

  const renderUserList = () => {
    return allUsers.map(user => (
      <div key={user.username} style={{ margin: '10px 0' }}>
        <span>{user.username}</span> {renderUserStatus(user)}
      </div>
    ));
  };

  const handleTabChange = (name) => {
    setTab(name);

    setUnreadMessages(prevUnreadMessages => {
        const newUnreadMessages = new Map(prevUnreadMessages);
        newUnreadMessages.delete(name);
        return newUnreadMessages;
    });

    if (name !== "CHATROOM") {
        loadPrivateChatHistory(name);
    }
};

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
    if (query.length > 0) {
      searchUsers(query);
    } else {
      fetchAllUsers();
    }
  };

  const onError = (err) => {
    console.log(err);
  }

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, message: value });
  }

  const sendValue = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: userData.username,
        message: userData.message,
        status: "MESSAGE"
      };
      stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: "" });
    }
  }

  const handleUsername = (event) => {
    const { name, value } = event.target;
    if (name === "username") {
      const filteredValue = value.replace(/[^a-zA-Z0-9\s]/g, '');
      setUserData({ ...userData, [name]: filteredValue });
    } else {
      setUserData({ ...userData, [name]: value });
      const sanitizedValue = DOMPurify.sanitize(value);
      setUserData({ ...userData, [name]: sanitizedValue });
    }
  };

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
  };

  const validateUsername = (username) => {
    const re = /^[a-zA-Z]+$/;
    return re.test(username);
  };

  const validateFields = () => {
    const errors = {};
    if (!userData.username || !validateUsername(userData.username)) {
      errors.username = "El nombre de usuario solo puede contener letras";
    }
    if (!userData.password) {
      errors.password = "La contraseña no puede estar vacía";
    }
    if (!userData.email || !validateEmail(userData.email)) {
      errors.email = "El email no es válido";
    }
    return errors;
  };

  const registerUser = async () => {
    const errors = validateFields();
    if (Object.keys(errors).length > 0) {
      setUserData({ ...userData, errors });
      return;
    }
    try {
      const response = await axios.post('http://localhost:8080/api/auth/register', {
        username: userData.username,
        password: userData.password,
        email: userData.email
      });
      console.log(response.data);
      setIsRegistering(false);
    } catch (error) {
      console.error('Registration failed:', error.response.data);
    }
  }

  const validateLoginFields = () => {
    const errors = {};
    if (!userData.username) {
      errors.username = "El nombre de usuario no puede estar vacío";
    }
    if (!userData.password) {
      errors.password = "La contraseña no puede estar vacía";
    }
    return errors;
  };
  
  const loginUser = async () => {
    const errors = validateLoginFields();
    if (Object.keys(errors).length > 0) {
      setUserData({ ...userData, errors });
      return;
    }
    try {
      const response = await axios.post('http://localhost:8080/api/auth/login', {
        username: userData.username,
        password: userData.password
      });
      console.log(response.data);
      setUserData({ ...userData, connected: true, errors: {} });
    } catch (error) {
      console.error('Login failed:', error.response.data);
      setUserData({ ...userData, errors: { ...errors, login: 'Nombre de usuario o contraseña incorrectos' } });
    }
  };

  const handleLogout = () => {
    setUserData({ ...userData, connected: false });
    stompClient.disconnect();
    window.location.href = "/login";  
  }
  
  return (
    <ChatRoomUI
      userData={userData}
      privateChats={privateChats}
      publicChats={publicChats}
      tab={tab}
      setTab={handleTabChange}
      handleMessage={handleMessage}
      sendValue={sendValue}
      sendPrivateValue={sendPrivateValue}
      handleUsername={handleUsername}
      registerUser={registerUser}
      loginUser={loginUser}
      unreadMessages={unreadMessages}
      isRegistering={isRegistering}
      setIsRegistering={setIsRegistering}
      allUsers={allUsers}
      searchQuery={searchQuery}
      setSearchQuery={setSearchQuery}
      searchUsers={searchUsers}
      renderUserStatus={searchUsers}
      renderUserList={searchUsers}
      logoutUser={handleLogout}
      onlineUsers={onlineUsers}
      isReconnecting={isReconnecting}

    />
  );
}

export default ChatRoom;
